mapper.update:
 update
	board
 set board_title = #{boardTitle}
   , board_content = #{boardContent}
 where board_no = #{boardNo}

 update
	attachment
 set origin_name = #{originName}
   , change_name = #{changeName}
 where ref_bno = #{refBno}

update place
   set count = count+1
   where p_no = #{ pno }

mapper.insert:
  insert 
	into place
	  (
	    p_no
	  , p_title
	  , P_CONTENT
	  , P_location
	  , P_ADDRESS
	  , p_writer
	  , p_img1
	  , p_img2
	  , p_img3
	  , p_img4
	  ) 
	  values
	  (
	   	SEQ_PLACENO.nextval
	  , #{ptitle}
	  , #{pcontent}
	  , #{plocation}
	  , #{paddress}
	  , #{pwriter}
	  , #{pimg1}
	  , #{pimg2}
	  , #{pimg3}
	  , #{pimg4}
	  )

INSERT INTO ATTACHMENT (FILE_NO, REF_BNO, FILE_level, ORIGIN_NAME, CHANGE_NAME, FILE_PATH) SELECT SEQ_FILE_NO.NEXTVAL, SEQ_PLACENO.CURRVAL,A.file_level, A.ORIGIN_NAME, A.CHANGE_NAME, A.FILE_PATH FROM (
SELECT #{attachment.fileLevel} AS file_level, #{attachment.originName} AS ORIGIN_NAME, #{attachment.changeName} AS CHANGE_NAME, #{attachment.filePath} AS FILE_PATH FROM DUAL
) A

mapper.update:
update
place
set
p_title = #{ptitle}
, p_content = #{pcontent}
, p_location = #{plocation}
, p_address = #{paddress}
, p_img1 = #{pimg1}
, p_img2 = #{pimg2}
, p_img3 = #{pimg3}
, p_img4 = #{pimg4}
where p_no = #{pno}

UPDATE ATTACHMENT
SET
    ORIGIN_NAME = #{originName},
    CHANGE_NAME = #{changeName},
    FILE_PATH = #{filePath}
WHERE FILE_NO = #{fileNo}

mapper.insert:
insert 
	into festival
	  (
	    FE_NO
	  , FE_TITLE
	  , FE_CONTENT
	  , FE_DATE
	  , FE_LOCATION
	  , CASH
	  , FE_TYPE
	  ,	LONGITUDE
	  ,	LATITUDE
	  ,	URL
	  ,	T_IMG
	  ,	HOST
	  ,	INQ
	  ,	AGE
	  ) 
	  values
	  (
	   	SEQ_FE_NO.nextval
	  , #{feTitle}
	  , #{feContent}
	  , #{feDate}
	  , #{feLocation}
	  , #{cash}
	  , #{feType}
	  , #{longitude}
	  , #{latitude}
	  , #{url}
	  , #{timg}
	  , #{host}
	  , #{inq}
	  , #{age}
	  )

mapper.update:
update
	   festival
   set fe_title = #{feTitle}
     , fe_content = #{feContent}
     , t_img = #{timg}
     , fe_location = #{feLocation}
     , cash = #{cash}
     , fe_date = #{feDate}
     , longitude = #{longitude}
     , latitude = #{latitude}
     , age = #{age}
     , host = #{host}
     , inq = #{inq}
     , age = #{age}
 where fe_no = #{feNo}
