mapper.insert:
insert
  into member
  	 (
  	   user_no 
  	 , user_id
  	 , user_pwd
  	 , user_name
  	 , nickname
  	 , email
  	 , phone
  	 , gender
  	 )
values 
     (
       SEQ_MNO.nextval
     , #{ userId }
     , #{ userPwd }
     , #{ userName }
     , #{ nickName }
     , #{ email }
     , #{ phone }
