mapper.insert:
  insert 
	into notice
	  (
	    notice_no
	  , notice_title
	  , notice_writer
	  , notice_content
	  ) 
	  values
	  (
	   	seq_bno.nextval
	  , #{noticeTitle}
	  , #{noticeWriter}
	  , #{noticeContent}
	  )

mapper.update:
update notice
   set count = count+1
   where notice_no = #{ noticeNo }

update notice
   set status = 'N'
   where notice_no = #{noticeNo}

update
	   notice
   set notice_title = #{noticeTitle}
     , notice_content = #{noticeContent}
 where notice_no = #{noticeNo}
 and notice_type = 1

update
	   notice
   set notice_title = #{noticeTitle}
     , notice_content = #{noticeContent}
 where notice_no = #{noticeNo}
 and notice_type = 2
