mapper.insert:
 	insert
		into cart
		(
			p_no
		,	user_no
		,	amount
		)
		values
		(
			#{pNo}
		,	#{userNo}
		,	#{amount}
		)

mapper.update:
	update cart
	   set amount = #{amount}
    where p_no = #{pNo}
      and user_no = #{userNo}

mapper.delete:
delete
    from cart
    where p_no = #{pNo}
    and user_no = #{userNo}

mapper.insert:
insert
	into orders
	(
		order_no
	,	user_no
	,	name
	,	address
	,	postcode
	,	phone
	,	order_date
	,	totalPrice
	,	o_msg
	,	d_msg
	)
	values
	(
		SEQ_ORDER_NO.nextval
	,	#{userNo}
	,	#{name}
	,	#{address}
	,	#{postcode}
	,	#{phone}
	,	sysdate
	,	#{totalPrice}
	,	#{oMsg}
	,	#{dMsg}
	)

insert
	into d_order
	(
		order_no
	,	p_no
	,	user_no
	,	amount
	,	p_type
	)
	values
	(
		SEQ_ORDER_NO.currval
	,	#{pNo}
	,	#{userNo}
	,	#{amount}
	,	#{pType}
	)

mapper.update:
update product
	set amount = amount - ${amount}
	where p_no = #{pNo}
